package com.datatype;

public class Laptop {
	static String brandName = "DELL";
	static byte ram = 4;
	static float screenSize = 15.1f;
	static int cost = 45000;
	static long serialNumber = 12489355521L;
	static double resolution = 15482.457256621;
	static char var = 'c';
	static boolean boo = true;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("The brand name is " + brandName);
		System.out.println("RAM " + ram);
		System.out.println("ScreenSize is :" + screenSize);
		System.out.println("The cost is :" + cost);
		System.out.println("The SerialNumber is :" + serialNumber);
		System.out.println("The Resolution " + resolution);
		System.out.println("The character is " + var);
		System.out.println("Boolean value is" + boo);

	}

}
