package com.switchcase;

public class NumberFoundOrNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=2;
switch(number)
{
case 0:
	System.out.println("The number 1 is present");
	break;
case 1:
	System.out.println("The number 2 is present");
	break;
case 2:
	System.out.println("The number 3 is present");	
	break;
default:
	System.out.println("The Number not found");}
	}

}
